            'slug'  => 'anahata-villa-spa-resort-2',
            'content' => '
            <p><span style="font-size:11pt"><span style="font-family:Arial"><span style="color:#000000"><strong>Bersepeda, Berkuda dan Yoga</strong></span></span></span></p>

