    public static function getAll()
    {
        return self::$articles;
    }

    public static function find($slug)
    {
        $articles = self::$articles;
        $result = [];

        foreach($articles as $article) {
            if ($article['slug'] == $slug) {
                $result = $article;
            }
        }

        return $result;
    }
